--
-- PostgreSQL database dump
--

SET statement_timeout = 0;
SET client_encoding = 'UTF8';
SET standard_conforming_strings = on;
SET check_function_bodies = false;
SET client_min_messages = warning;

SET search_path = _dba_, pg_catalog;

SET default_tablespace = '';

SET default_with_oids = false;

--
-- Name: tmp_policies; Type: TABLE; Schema: _dba_; Owner: psql_dba; Tablespace: 
--

CREATE TABLE tmp_policies (
    caption character varying(500),
    enabled boolean,
    content character varying(300000)
);


-- ALTER TABLE _dba_.tmp_policies OWNER TO psql_dba;

--
-- Data for Name: tmp_policies; Type: TABLE DATA; Schema: _dba_; Owner: psql_dba
--

COPY tmp_policies (caption, enabled, content) FROM stdin;
